                }
                else
                {
                    // g8
                    SelectCellRange(
                        anchorColumnIndex,
                        oldEdgeRowIndex,
                        oldEdgeColumnIndex,
                        anchorRowIndex,
                        select: false);
                    SelectCellRange(
                        newEdgeColumnIndex,
                        newEdgeRowIndex,
                        anchorColumnIndex,
                        anchorRowIndex,
