    /// <summary>
    ///  Handles the WM_CONTEXTMENU message
    /// </summary>
    internal override void WmContextMenu(ref Message m)
    {
        ContextMenuStrip? contextMenuStrip;
        Point client;
        bool keyboardActivated = false;

        // lparam will be -1 when the user invokes the context menu with the keyboard.
        if (m.LParamInternal == -1)
        {
            keyboardActivated = true;
            client = new Point(Width / 2, Height / 2);
            contextMenuStrip = ContextMenuStrip;
        }
        else
        {
            client = PointToClient(PARAM.ToPoint(m.LParamInternal));
            HitTestInfo hti = HitTest(client.X, client.Y);
            DataGridViewCell? dataGridViewCell = null;
            switch (hti.Type)
            {
                case DataGridViewHitTestType.Cell:
                    dataGridViewCell = Rows.SharedRow(hti._row).Cells[hti._col];
                    break;
                case DataGridViewHitTestType.ColumnHeader:
                    Debug.Assert(hti._row == -1);
                    dataGridViewCell = Columns[hti._col].HeaderCell;
                    break;
                case DataGridViewHitTestType.RowHeader:
                    Debug.Assert(hti._col == -1);
                    dataGridViewCell = Rows.SharedRow(hti._row).HeaderCell;
                    break;
                case DataGridViewHitTestType.TopLeftHeader:
                    Debug.Assert(hti._row == -1);
                    Debug.Assert(hti._col == -1);
                    dataGridViewCell = TopLeftHeaderCell;
                    break;
            }

            if (dataGridViewCell is not null)
            {
                contextMenuStrip = dataGridViewCell.GetInheritedContextMenuStrip(hti._row);
            }
            else
            {
                contextMenuStrip = ContextMenuStrip;
            }
        }

        // VisualStudio7 # 156, only show the context menu when clicked in the client area
        if (contextMenuStrip is not null && ClientRectangle.Contains(client))
        {
            contextMenuStrip.ShowInternal(this, client, keyboardActivated);
        }
        else
        {
            DefWndProc(ref m);
        }
    }

    /// <summary>
    ///  Handles the WM_GETDLGCODE message
    /// </summary>
    private void WmGetDlgCode(ref Message m)
    {
        m.ResultInternal = (LRESULT)(m.ResultInternal | (nint)PInvoke.DLGC_WANTARROWS | (nint)PInvoke.DLGC_WANTCHARS);

        Keys modifierKeys = ModifierKeys;
        if (GetTabKeyEffective((modifierKeys & Keys.Shift) == Keys.Shift, (modifierKeys & Keys.Control) == Keys.Control))
        {
            m.ResultInternal = (LRESULT)(m.ResultInternal | (nint)PInvoke.DLGC_WANTTAB);
        }
    }

    private unsafe bool WmNotify(ref Message m)
    {
        if (m.LParamInternal == 0)
        {
            return false;
        }

        NMHDR* nmhdr = (NMHDR*)(nint)m.LParamInternal;
        if (nmhdr->code == PInvoke.TTN_GETDISPINFOW && !DesignMode)
        {
            string toolTip = ToolTipPrivate;

            if (!string.IsNullOrEmpty(toolTip))
            {
                // Setting the max width has the added benefit of enabling multiline tool tips
                PInvoke.SendMessage(nmhdr->hwndFrom, PInvoke.TTM_SETMAXTIPWIDTH, 0, SystemInformation.MaxWindowTrackSize.Width);

                NMTTDISPINFOW* ttt = (NMTTDISPINFOW*)(nint)m.LParamInternal;
                _toolTipBuffer.SetText(toolTip);
                ttt->lpszText = (char*)_toolTipBuffer.Buffer;
                ttt->hinst = HINSTANCE.Null;

                if (RightToLeft == RightToLeft.Yes)
                {
                    ttt->uFlags |= TOOLTIP_FLAGS.TTF_RTLREADING;
                }

                return true;
            }
        }

        return false;
    }

    protected override void WndProc(ref Message m)
    {
        switch (m.MsgInternal)
        {
            case PInvoke.WM_GETDLGCODE:
                WmGetDlgCode(ref m);
                return;
            case PInvoke.WM_LBUTTONDBLCLK:
            case PInvoke.WM_LBUTTONDOWN:
                // If the OnEnter procedure is called, it's because of a mouse down event, and not a TAB key.
                _dataGridViewOper[OperationInMouseDown] = true;
                try
                {
                    base.WndProc(ref m);
                }
                finally
                {
                    _dataGridViewOper[OperationInMouseDown] = false;
                }

                return;
            case PInvoke.WM_NOTIFY:
                if (WmNotify(ref m))
                {
                    // we are done - skip default handling
                    return;
                }

                break;

            case PInvoke.WM_IME_STARTCOMPOSITION:
            case PInvoke.WM_IME_COMPOSITION:
                if (EditingControl is not null)
                {
                    // Make sure that the first character is forwarded to the editing control.
                    PInvoke.SendMessage(EditingControl, m.MsgInternal, m.WParamInternal, m.LParamInternal);
                }

                break;
        }

        base.WndProc(ref m);
    }

    /// <summary>
    ///  Helper that  gets the specified event if the class is not disposing or disposed.
    /// </summary>
    private T? GetEvent<T>(object staticEvent)
        where T : class =>
        _dataGridViewOper[OperationInDispose] || IsDisposed ? null : Events[staticEvent] as T;

    private static void ThrowInvalidOperationExceptionIfNull<T>([NotNull] T? instance)
        where T : class
    {
        if (instance is null)
        {
            throw new InvalidOperationException();
        }
    }
}
