            OnColumnHeaderGlobalAutoSize(e.Column.Index);

            if (Events[EVENT_DATAGRIDVIEWCOLUMNHEADERCELLCHANGED] is DataGridViewColumnEventHandler eh && !dataGridViewOper[DATAGRIDVIEWOPER_inDispose] && !IsDisposed)
