                            Debug.Assert(success);
                        }
                        return true;
                }
            }
            finally
            {
                NoSelectionChangeCount--;
            }
            return true;
        }

        protected bool ProcessNextKey(Keys keyData)
        {
            bool success;
            DataGridViewColumn dataGridViewColumn = Columns.GetFirstColumn(DataGridViewElementStates.Visible);
            int firstVisibleColumnIndex = (dataGridViewColumn == null) ? -1 : dataGridViewColumn.Index;
            if (firstVisibleColumnIndex == -1)
            {
                return false;
            }
            int nextScreenVisibleRowIndexTmp, nextScreenVisibleRowIndex = -1, jumpRows = 0;
            if (ptCurrentCell.Y == -1)
            {
                nextScreenVisibleRowIndex = Rows.GetFirstRow(DataGridViewElementStates.Visible);
                if (nextScreenVisibleRowIndex == -1)
                {
                    return false;
                }
            }
            else
            {
                nextScreenVisibleRowIndex = ptCurrentCell.Y;
            }

            if ((Rows.GetRowState(nextScreenVisibleRowIndex) & DataGridViewElementStates.Frozen) != 0)
            {
                if (displayedBandsInfo.FirstDisplayedScrollingRow > 0)
                {
                    int firstDisplayedScrollingRowIndex = Rows.GetFirstRow(DataGridViewElementStates.Visible, DataGridViewElementStates.Frozen);
                    Debug.Assert(firstDisplayedScrollingRowIndex != -1);
                    if (!ScrollIntoView(ptCurrentCell.X == -1 ? firstVisibleColumnIndex : ptCurrentCell.X,
                        firstDisplayedScrollingRowIndex, true))
                    {
                        return true;
                    }
                    jumpRows = Rows.GetRowCount(DataGridViewElementStates.Visible,
                        ptCurrentCell.Y,
                        firstDisplayedScrollingRowIndex) - 1;
                }
                else
                {
                    jumpRows = displayedBandsInfo.NumTotallyDisplayedFrozenRows;
                }
            }
            jumpRows += displayedBandsInfo.NumTotallyDisplayedScrollingRows;

            nextScreenVisibleRowIndexTmp = nextScreenVisibleRowIndex;
            Debug.Assert(nextScreenVisibleRowIndexTmp != -1);
            if (jumpRows == 0)
            {
                jumpRows = 1;
            }
            while (jumpRows > 0 && nextScreenVisibleRowIndexTmp != -1)
            {
                nextScreenVisibleRowIndexTmp = Rows.GetNextRow(nextScreenVisibleRowIndex, DataGridViewElementStates.Visible);
                if (nextScreenVisibleRowIndexTmp != -1)
                {
                    nextScreenVisibleRowIndex = nextScreenVisibleRowIndexTmp;
                    jumpRows--;
                }
            }

            noSelectionChangeCount++;
            try
            {
                if (ptCurrentCell.X == -1)
                {
                    ClearSelection();
                    switch (SelectionMode)
                    {
                        case DataGridViewSelectionMode.CellSelect:
                        case DataGridViewSelectionMode.ColumnHeaderSelect:
                        case DataGridViewSelectionMode.RowHeaderSelect:
                            SetSelectedCellCore(firstVisibleColumnIndex, nextScreenVisibleRowIndex, true);
                            break;
                        case DataGridViewSelectionMode.FullRowSelect:
                            SetSelectedRowCore(nextScreenVisibleRowIndex, true);
                            break;
                        case DataGridViewSelectionMode.FullColumnSelect:
                            SetSelectedColumnCore(firstVisibleColumnIndex, true);
                            break;
                    }
                    success = ScrollIntoView(firstVisibleColumnIndex, nextScreenVisibleRowIndex, false);
                    Debug.Assert(success);
                    if (IsInnerCellOutOfBounds(firstVisibleColumnIndex, nextScreenVisibleRowIndex))
                    {
                        return true;
                    }

                    SetCurrentCellAddressCore(firstVisibleColumnIndex, nextScreenVisibleRowIndex, true, false, false);
                    return true;
                }

                switch (SelectionMode)
                {
                    case DataGridViewSelectionMode.CellSelect:
                    case DataGridViewSelectionMode.ColumnHeaderSelect:
                        if (!ScrollIntoView(ptCurrentCell.X, nextScreenVisibleRowIndex, true))
                        {
                            return true;
                        }
                        if (ptCurrentCell.X == -1 || IsRowOutOfBounds(nextScreenVisibleRowIndex))
                        {
                            return true;
                        }
                        if ((keyData & Keys.Shift) == Keys.Shift && MultiSelect)
                        {
                            Debug.Assert(ptAnchorCell.Y >= 0);
                            int oldEdgeColumnIndex = ptCurrentCell.X;
                            int oldEdgeRowIndex = ptCurrentCell.Y;
                            if (ptAnchorCell.Y == -1)
                            {
                                return true;
                            }
                            UpdateSelectedCellsBlock(ptAnchorCell.X, ref oldEdgeColumnIndex, oldEdgeColumnIndex,
                                ptAnchorCell.Y, ref oldEdgeRowIndex, nextScreenVisibleRowIndex);
                        }
                        else
                        {
                            ClearSelection();
                            SetSelectedCellCore(ptCurrentCell.X, nextScreenVisibleRowIndex, true);
                        }
                        success = SetCurrentCellAddressCore(ptCurrentCell.X, nextScreenVisibleRowIndex, false, false, false);
                        Debug.Assert(success);
                        return true;

                    case DataGridViewSelectionMode.FullRowSelect:
                        if (!ScrollIntoView(ptCurrentCell.X, nextScreenVisibleRowIndex, true))
                        {
                            return true;
                        }
                        if (ptCurrentCell.X == -1 || IsRowOutOfBounds(nextScreenVisibleRowIndex))
                        {
                            return true;
                        }
                        ClearSelection();
                        if ((keyData & Keys.Shift) == Keys.Shift && MultiSelect)
                        {
                            Debug.Assert(ptAnchorCell.Y >= 0);
                            if (ptAnchorCell.Y == -1)
                            {
                                return true;
                            }
                            if (ptAnchorCell.Y < nextScreenVisibleRowIndex)
                            {
                                SelectRowRange(ptAnchorCell.Y, nextScreenVisibleRowIndex, true);
                            }
                            else
                            {
                                SelectRowRange(nextScreenVisibleRowIndex, ptAnchorCell.Y, true);
                            }
                        }
                        else
                        {
                            SetSelectedRowCore(nextScreenVisibleRowIndex, true);
                        }
                        success = SetCurrentCellAddressCore(ptCurrentCell.X, nextScreenVisibleRowIndex, false, false, false);
                        return true;

                    case DataGridViewSelectionMode.RowHeaderSelect:
                        if (!ScrollIntoView(ptCurrentCell.X, nextScreenVisibleRowIndex, true))
                        {
                            return true;
                        }
                        if (ptCurrentCell.X == -1 || IsRowOutOfBounds(nextScreenVisibleRowIndex))
                        {
                            return true;
                        }
                        if ((keyData & Keys.Shift) == Keys.Shift && MultiSelect)
                        {
                            Debug.Assert(ptAnchorCell.Y >= 0);
                            if (ptAnchorCell.Y == -1)
                            {
                                return true;
                            }
                            if ((Rows.GetRowState(ptCurrentCell.Y) & DataGridViewElementStates.Selected) != 0)
                            {
                                ClearSelection();
                                if (ptAnchorCell.Y < nextScreenVisibleRowIndex)
                                {
                                    SelectRowRange(ptAnchorCell.Y, nextScreenVisibleRowIndex, true);
                                }
                                else
                                {
                                    SelectRowRange(nextScreenVisibleRowIndex, ptAnchorCell.Y, true);
                                }
                            }
                            else
                            {
                                int oldEdgeColumnIndex = ptCurrentCell.X;
                                int oldEdgeRowIndex = ptCurrentCell.Y;
                                UpdateSelectedCellsBlock(ptAnchorCell.X, ref oldEdgeColumnIndex, oldEdgeColumnIndex,
                                    ptAnchorCell.Y, ref oldEdgeRowIndex, nextScreenVisibleRowIndex);
                            }
                        }
                        else
                        {
                            if ((Rows.GetRowState(ptCurrentCell.Y) & DataGridViewElementStates.Selected) != 0)
                            {
                                ClearSelection();
                                SetSelectedRowCore(nextScreenVisibleRowIndex, true);
                            }
                            else
                            {
                                ClearSelection();
                                SetSelectedCellCore(ptCurrentCell.X, nextScreenVisibleRowIndex, true);
                            }
                        }
                        success = SetCurrentCellAddressCore(ptCurrentCell.X, nextScreenVisibleRowIndex, !MultiSelect, false, false);
                        return true;

                    case DataGridViewSelectionMode.FullColumnSelect:
                        if (!ScrollIntoView(ptCurrentCell.X, nextScreenVisibleRowIndex, true))
                        {
                            return true;
                        }
                        if (ptCurrentCell.X == -1 || IsRowOutOfBounds(nextScreenVisibleRowIndex))
                        {
                            return true;
                        }
                        success = SetCurrentCellAddressCore(ptCurrentCell.X, nextScreenVisibleRowIndex, true, false, false);
                        return true;
                }
            }
            finally
            {
                NoSelectionChangeCount--;
            }
            return true;
        }

        protected bool ProcessPriorKey(Keys keyData)
        {
            DataGridViewColumn dataGridViewColumn = Columns.GetFirstColumn(DataGridViewElementStates.Visible);
            int firstVisibleColumnIndex = (dataGridViewColumn == null) ? -1 : dataGridViewColumn.Index;
            if (firstVisibleColumnIndex == -1)
            {
                return false;
            }
            int previousScreenVisibleRowIndexTmp, previousScreenVisibleRowIndex = -1;
            if (ptCurrentCell.Y == -1)
            {
                previousScreenVisibleRowIndex = Rows.GetFirstRow(DataGridViewElementStates.Visible);
                if (previousScreenVisibleRowIndex == -1)
                {
                    return false;
                }
            }
            else
            {
                previousScreenVisibleRowIndex = ptCurrentCell.Y;
            }

            int jumpRows;
            if ((Rows.GetRowState(previousScreenVisibleRowIndex) & DataGridViewElementStates.Frozen) != 0)
            {
                jumpRows = displayedBandsInfo.NumTotallyDisplayedFrozenRows;
            }
            else
            {
                jumpRows = displayedBandsInfo.NumTotallyDisplayedScrollingRows;
            }
            if (jumpRows == 0)
            {
                jumpRows = 1;
            }
            previousScreenVisibleRowIndexTmp = previousScreenVisibleRowIndex;
            Debug.Assert(previousScreenVisibleRowIndexTmp != -1);
            while (jumpRows > 0 && previousScreenVisibleRowIndexTmp != -1)
            {
                previousScreenVisibleRowIndexTmp = Rows.GetPreviousRow(previousScreenVisibleRowIndex, DataGridViewElementStates.Visible);
                if (previousScreenVisibleRowIndexTmp != -1)
                {
                    previousScreenVisibleRowIndex = previousScreenVisibleRowIndexTmp;
                }
                jumpRows--;
            }

            if ((Rows.GetRowState(previousScreenVisibleRowIndex) & DataGridViewElementStates.Frozen) != 0)
            {
                // Make sure the first scrolling row is visible if any
                int firstDisplayedScrollingRowIndex = Rows.GetFirstRow(DataGridViewElementStates.Visible, DataGridViewElementStates.Frozen);
                if (firstDisplayedScrollingRowIndex != -1 &&
                    !ScrollIntoView(ptCurrentCell.X == -1 ? firstVisibleColumnIndex : ptCurrentCell.X,
                    firstDisplayedScrollingRowIndex, true))
                {
                    return true;
                }
                // Also, first visible frozen row should become current one - there is no reason to jump to another frozen row.
                previousScreenVisibleRowIndex = Rows.GetFirstRow(DataGridViewElementStates.Visible | DataGridViewElementStates.Frozen);
            }
            Debug.Assert(previousScreenVisibleRowIndex != -1);

            noSelectionChangeCount++;
            try
            {
                if (ptCurrentCell.X == -1)
                {
                    ClearSelection();
                    switch (SelectionMode)
                    {
                        case DataGridViewSelectionMode.CellSelect:
                        case DataGridViewSelectionMode.ColumnHeaderSelect:
                        case DataGridViewSelectionMode.RowHeaderSelect:
                            SetSelectedCellCore(firstVisibleColumnIndex, previousScreenVisibleRowIndex, true);
                            break;
                        case DataGridViewSelectionMode.FullRowSelect:
                            SetSelectedRowCore(previousScreenVisibleRowIndex, true);
                            break;
                        case DataGridViewSelectionMode.FullColumnSelect:
                            SetSelectedColumnCore(firstVisibleColumnIndex, true);
                            break;
                    }

                    ScrollIntoView(firstVisibleColumnIndex, previousScreenVisibleRowIndex, false);
                    if (IsInnerCellOutOfBounds(firstVisibleColumnIndex, previousScreenVisibleRowIndex))
                    {
                        return true;
                    }

                    SetCurrentCellAddressCore(firstVisibleColumnIndex, previousScreenVisibleRowIndex, true, false, false);
                    return true;
                }

                switch (SelectionMode)
                {
                    case DataGridViewSelectionMode.CellSelect:
                    case DataGridViewSelectionMode.ColumnHeaderSelect:
                        if (!ScrollIntoView(ptCurrentCell.X, previousScreenVisibleRowIndex, true))
                        {
                            return true;
                        }
                        if (ptCurrentCell.X == -1 || IsRowOutOfBounds(previousScreenVisibleRowIndex))
                        {
                            return true;
                        }
                        if ((keyData & Keys.Shift) == Keys.Shift && MultiSelect)
                        {
                            Debug.Assert(ptAnchorCell.Y >= 0);
                            int oldEdgeColumnIndex = ptCurrentCell.X;
                            int oldEdgeRowIndex = ptCurrentCell.Y;
                            if (ptAnchorCell.X == -1)
                            {
                                return true;
                            }
                            UpdateSelectedCellsBlock(ptAnchorCell.X, ref oldEdgeColumnIndex, oldEdgeColumnIndex,
                                ptAnchorCell.Y, ref oldEdgeRowIndex, previousScreenVisibleRowIndex);
                        }
                        else
                        {
                            ClearSelection();
                            SetSelectedCellCore(ptCurrentCell.X, previousScreenVisibleRowIndex, true);
                        }
                        SetCurrentCellAddressCore(ptCurrentCell.X, previousScreenVisibleRowIndex, false, false, false);
                        return true;

                    case DataGridViewSelectionMode.FullRowSelect:
                        if (!ScrollIntoView(ptCurrentCell.X, previousScreenVisibleRowIndex, true))
                        {
                            return true;
                        }
                        if (ptCurrentCell.X == -1 || IsRowOutOfBounds(previousScreenVisibleRowIndex))
                        {
                            return true;
                        }
                        ClearSelection();
                        if ((keyData & Keys.Shift) == Keys.Shift && MultiSelect)
                        {
                            Debug.Assert(ptAnchorCell.Y >= 0);
                            if (ptAnchorCell.Y == -1)
                            {
                                return true;
                            }
                            if (ptAnchorCell.Y < previousScreenVisibleRowIndex)
                            {
                                SelectRowRange(ptAnchorCell.Y, previousScreenVisibleRowIndex, true);
                            }
                            else
                            {
                                SelectRowRange(previousScreenVisibleRowIndex, ptAnchorCell.Y, true);
                            }
                        }
                        else
                        {
                            SetSelectedRowCore(previousScreenVisibleRowIndex, true);
                        }
                        SetCurrentCellAddressCore(ptCurrentCell.X, previousScreenVisibleRowIndex, false, false, false);
                        return true;

                    case DataGridViewSelectionMode.RowHeaderSelect:
                        if (!ScrollIntoView(ptCurrentCell.X, previousScreenVisibleRowIndex, true))
                        {
                            return true;
                        }
                        if (ptCurrentCell.X == -1 || IsRowOutOfBounds(previousScreenVisibleRowIndex))
                        {
                            return true;
                        }
                        if ((keyData & Keys.Shift) == Keys.Shift && MultiSelect)
                        {
                            Debug.Assert(ptAnchorCell.Y >= 0);
                            if (ptAnchorCell.Y == -1)
                            {
                                return true;
                            }
                            if ((Rows.GetRowState(ptCurrentCell.Y) & DataGridViewElementStates.Selected) != 0)
                            {
                                ClearSelection();
                                if (ptAnchorCell.Y < previousScreenVisibleRowIndex)
                                {
                                    SelectRowRange(ptAnchorCell.Y, previousScreenVisibleRowIndex, true);
                                }
                                else
                                {
                                    SelectRowRange(previousScreenVisibleRowIndex, ptAnchorCell.Y, true);
                                }
                            }
                            else
                            {
                                int oldEdgeColumnIndex = ptCurrentCell.X;
                                int oldEdgeRowIndex = ptCurrentCell.Y;
                                UpdateSelectedCellsBlock(ptAnchorCell.X, ref oldEdgeColumnIndex, oldEdgeColumnIndex,
                                    ptAnchorCell.Y, ref oldEdgeRowIndex, previousScreenVisibleRowIndex);
                            }
                        }
                        else
                        {
                            if ((Rows.GetRowState(ptCurrentCell.Y) & DataGridViewElementStates.Selected) != 0)
                            {
                                ClearSelection();
                                SetSelectedRowCore(previousScreenVisibleRowIndex, true);
                            }
                            else
                            {
                                ClearSelection();
                                SetSelectedCellCore(ptCurrentCell.X, previousScreenVisibleRowIndex, true);
                            }
                        }

                        SetCurrentCellAddressCore(ptCurrentCell.X, previousScreenVisibleRowIndex, !MultiSelect, false, false);
                        return true;

                    case DataGridViewSelectionMode.FullColumnSelect:
                        if (!ScrollIntoView(ptCurrentCell.X, previousScreenVisibleRowIndex, true))
                        {
                            return true;
                        }
                        if (ptCurrentCell.X == -1 || IsRowOutOfBounds(previousScreenVisibleRowIndex))
                        {
                            return true;
                        }

                        SetCurrentCellAddressCore(ptCurrentCell.X, previousScreenVisibleRowIndex, true, false, false);
                        return true;
                }
            }
            finally
            {
                NoSelectionChangeCount--;
            }
            return true;
        }

        protected bool ProcessRightKey(Keys keyData)
        {
            if (RightToLeftInternal)
            {
                return ProcessLeftKeyPrivate(keyData);
            }
            else
            {
                return ProcessRightKeyPrivate(keyData);
            }
        }

        private bool ProcessRightKeyPrivate(Keys keyData)
        {
            bool? resizeResult = ProcessColumnResize(keyData, keyboardResizeStep);
            if (resizeResult.HasValue)
            {
                return resizeResult.Value;
            }

            bool success;
            DataGridViewColumn dataGridViewColumn = Columns.GetLastColumn(DataGridViewElementStates.Visible,
                DataGridViewElementStates.None);
            int lastVisibleColumnIndex = (dataGridViewColumn == null) ? -1 : dataGridViewColumn.Index;
            int firstVisibleRowIndex = Rows.GetFirstRow(DataGridViewElementStates.Visible);
            if (lastVisibleColumnIndex == -1 || firstVisibleRowIndex == -1)
            {
                return false;
            }
            int nextVisibleColumnIndex = -1;
            if (ptCurrentCell.X != -1)
            {
                dataGridViewColumn = Columns.GetNextColumn(Columns[ptCurrentCell.X],
                    DataGridViewElementStates.Visible,
                    DataGridViewElementStates.None);
                if (dataGridViewColumn != null)
                {
                    nextVisibleColumnIndex = dataGridViewColumn.Index;
                }
            }

            noSelectionChangeCount++;
            try
            {
                switch (SelectionMode)
                {
                    case DataGridViewSelectionMode.CellSelect:
                    case DataGridViewSelectionMode.RowHeaderSelect:
                        if ((keyData & Keys.Control) == Keys.Control)
                        {
                            return ProcessRightMost((keyData & Keys.Shift) == Keys.Shift, lastVisibleColumnIndex, firstVisibleRowIndex);
                        }
                        else
                        {
                            if ((keyData & Keys.Shift) == Keys.Shift)
                            {
                                if (ptCurrentCell.X == -1)
                                {
                                    ClearSelection();
                                    SetSelectedCellCore(lastVisibleColumnIndex, firstVisibleRowIndex, true);
                                    success = ScrollIntoView(lastVisibleColumnIndex, firstVisibleRowIndex, false);
                                    Debug.Assert(success);
                                    if (IsInnerCellOutOfBounds(lastVisibleColumnIndex, firstVisibleRowIndex))
                                    {
                                        return true;
                                    }
                                    success = SetCurrentCellAddressCore(lastVisibleColumnIndex, firstVisibleRowIndex, true, false, false);
                                    Debug.Assert(success);
                                }
                                else
                                {
                                    if (nextVisibleColumnIndex == -1)
                                    {
                                        return true;
                                    }
                                    if (!ScrollIntoView(nextVisibleColumnIndex, ptCurrentCell.Y, true))
                                    {
                                        return true;
                                    }
                                    if (ptCurrentCell.Y == -1 || IsColumnOutOfBounds(nextVisibleColumnIndex))
                                    {
                                        return true;
                                    }
                                    if (MultiSelect)
                                    {
                                        int oldEdgeColumnIndex = ptCurrentCell.X;
                                        int oldEdgeRowIndex = ptCurrentCell.Y;
                                        if (ptAnchorCell.X == -1)
                                        {
                                            return true;
                                        }
                                        UpdateSelectedCellsBlock(ptAnchorCell.X, ref oldEdgeColumnIndex, nextVisibleColumnIndex,
                                            ptAnchorCell.Y, ref oldEdgeRowIndex, oldEdgeRowIndex);
                                    }
                                    else
                                    {
                                        ClearSelection();
                                        SetSelectedCellCore(nextVisibleColumnIndex, ptCurrentCell.Y, true);
                                    }
                                    success = SetCurrentCellAddressCore(nextVisibleColumnIndex, ptCurrentCell.Y, !MultiSelect, false, false);
                                    Debug.Assert(success);
                                }
                            }
                            else
                            {
                                if (ptCurrentCell.X == -1)
                                {
                                    ClearSelection();
                                    SetSelectedCellCore(lastVisibleColumnIndex, firstVisibleRowIndex, true);
                                    success = ScrollIntoView(lastVisibleColumnIndex, firstVisibleRowIndex, false);
                                    Debug.Assert(success);
                                    if (IsInnerCellOutOfBounds(lastVisibleColumnIndex, firstVisibleRowIndex))
                                    {
                                        return true;
                                    }
                                    success = SetCurrentCellAddressCore(lastVisibleColumnIndex, firstVisibleRowIndex, true, false, false);
                                    Debug.Assert(success);
                                }
                                else
                                {
                                    if (nextVisibleColumnIndex == -1)
                                    {
                                        return true;
                                    }
                                    if (!ScrollIntoView(nextVisibleColumnIndex, ptCurrentCell.Y, true))
                                    {
                                        return true;
                                    }
                                    if (ptCurrentCell.Y == -1 || IsColumnOutOfBounds(nextVisibleColumnIndex))
                                    {
                                        return true;
                                    }
                                    ClearSelection();
                                    SetSelectedCellCore(nextVisibleColumnIndex, ptCurrentCell.Y, true);
                                    success = SetCurrentCellAddressCore(nextVisibleColumnIndex, ptCurrentCell.Y, true, false, false);
                                    Debug.Assert(success);
                                }
                            }
                        }
                        return true;

                    case DataGridViewSelectionMode.FullColumnSelect:
                        if ((keyData & Keys.Control) == Keys.Control)
                        {
                            return ProcessRightMost((keyData & Keys.Shift) == Keys.Shift, lastVisibleColumnIndex, firstVisibleRowIndex);
                        }
                        else
                        {
                            if ((keyData & Keys.Shift) == Keys.Shift)
                            {
                                if (ptCurrentCell.X == -1)
                                {
                                    ClearSelection();
                                    SetSelectedColumnCore(lastVisibleColumnIndex, true);
                                    success = ScrollIntoView(lastVisibleColumnIndex, firstVisibleRowIndex, false);
                                    Debug.Assert(success);
                                    if (IsInnerCellOutOfBounds(lastVisibleColumnIndex, firstVisibleRowIndex))
                                    {
                                        return true;
                                    }
                                    success = SetCurrentCellAddressCore(lastVisibleColumnIndex, firstVisibleRowIndex, true, false, false);
                                    Debug.Assert(success);
                                }
                                else
                                {
                                    if (nextVisibleColumnIndex == -1)
                                    {
                                        return true;
                                    }
                                    if (!ScrollIntoView(nextVisibleColumnIndex, ptCurrentCell.Y, true))
                                    {
                                        return true;
                                    }
                                    if (ptCurrentCell.Y == -1 || IsColumnOutOfBounds(nextVisibleColumnIndex))
                                    {
                                        return true;
                                    }
                                    ClearSelection();
                                    if (MultiSelect)
                                    {
                                        if (ptAnchorCell.X == -1)
                                        {
                                            return true;
                                        }
                                        if (Columns.DisplayInOrder(ptAnchorCell.X, nextVisibleColumnIndex))
                                        {
                                            SelectColumnRange(ptAnchorCell.X, nextVisibleColumnIndex, true);
                                        }
                                        else
                                        {
                                            SelectColumnRange(nextVisibleColumnIndex, ptAnchorCell.X, true);
                                        }
                                    }
                                    else
                                    {
                                        SetSelectedColumnCore(nextVisibleColumnIndex, true);
                                    }
                                    success = SetCurrentCellAddressCore(nextVisibleColumnIndex, ptCurrentCell.Y, !MultiSelect, false, false);
                                    Debug.Assert(success);
                                }
                            }
                            else
                            {
                                if (ptCurrentCell.X == -1)
                                {
                                    ClearSelection();
                                    SetSelectedColumnCore(lastVisibleColumnIndex, true);
                                    success = ScrollIntoView(lastVisibleColumnIndex, firstVisibleRowIndex, false);
                                    Debug.Assert(success);
                                    if (IsInnerCellOutOfBounds(lastVisibleColumnIndex, firstVisibleRowIndex))
                                    {
                                        return true;
                                    }
                                    success = SetCurrentCellAddressCore(lastVisibleColumnIndex, firstVisibleRowIndex, true, false, false);
                                    Debug.Assert(success);
                                }
                                else
                                {
                                    if (nextVisibleColumnIndex == -1)
                                    {
                                        return true;
                                    }
                                    if (!ScrollIntoView(nextVisibleColumnIndex, ptCurrentCell.Y, true))
                                    {
                                        return true;
                                    }
                                    if (ptCurrentCell.Y == -1 || IsColumnOutOfBounds(nextVisibleColumnIndex))
                                    {
                                        return true;
                                    }
                                    ClearSelection();
                                    SetSelectedColumnCore(nextVisibleColumnIndex, true);
                                    success = SetCurrentCellAddressCore(nextVisibleColumnIndex, ptCurrentCell.Y, true, false, false);
                                    Debug.Assert(success);
                                }
                            }
                        }
                        return true;

                    case DataGridViewSelectionMode.ColumnHeaderSelect:
                        if ((keyData & Keys.Control) == Keys.Control)
                        {
                            return ProcessRightMost((keyData & Keys.Shift) == Keys.Shift, lastVisibleColumnIndex, firstVisibleRowIndex);
                        }
                        else
                        {
                            if ((keyData & Keys.Shift) == Keys.Shift)
                            {
                                if (ptCurrentCell.X == -1)
                                {
                                    ClearSelection();
                                    SetSelectedCellCore(lastVisibleColumnIndex, firstVisibleRowIndex, true);
                                    success = ScrollIntoView(lastVisibleColumnIndex, firstVisibleRowIndex, false);
                                    Debug.Assert(success);
                                    if (IsInnerCellOutOfBounds(lastVisibleColumnIndex, firstVisibleRowIndex))
                                    {
                                        return true;
                                    }
                                    success = SetCurrentCellAddressCore(lastVisibleColumnIndex, firstVisibleRowIndex, true, false, false);
                                    Debug.Assert(success);
                                }
                                else
                                {
                                    if (nextVisibleColumnIndex == -1)
                                    {
                                        return true;
                                    }
                                    if (!ScrollIntoView(nextVisibleColumnIndex, ptCurrentCell.Y, true))
                                    {
                                        return true;
                                    }
                                    if (ptCurrentCell.Y == -1 || IsColumnOutOfBounds(nextVisibleColumnIndex))
                                    {
                                        return true;
                                    }
                                    if (Columns[ptCurrentCell.X].Selected)
                                    {
                                        ClearSelection();
                                        if (MultiSelect)
                                        {
                                            if (ptAnchorCell.X == -1)
                                            {
                                                return true;
                                            }
                                            if (Columns.DisplayInOrder(ptAnchorCell.X, nextVisibleColumnIndex))
                                            {
                                                SelectColumnRange(ptAnchorCell.X, nextVisibleColumnIndex, true);
                                            }
                                            else
                                            {
                                                SelectColumnRange(nextVisibleColumnIndex, ptAnchorCell.X, true);
                                            }
                                        }
                                        else
                                        {
                                            SetSelectedColumnCore(nextVisibleColumnIndex, true);
                                        }
                                    }
                                    else
                                    {
                                        if (MultiSelect)
                                        {
                                            if (ptAnchorCell.X == -1)
                                            {
                                                return true;
                                            }
                                            int oldEdgeColumnIndex = ptCurrentCell.X;
                                            int oldEdgeRowIndex = ptCurrentCell.Y;
                                            UpdateSelectedCellsBlock(ptAnchorCell.X, ref oldEdgeColumnIndex, nextVisibleColumnIndex,
                                                ptAnchorCell.Y, ref oldEdgeRowIndex, oldEdgeRowIndex);
                                        }
                                        else
                                        {
                                            ClearSelection();
                                            SetSelectedCellCore(nextVisibleColumnIndex, ptCurrentCell.Y, true);
                                        }
                                    }
                                    success = SetCurrentCellAddressCore(nextVisibleColumnIndex, ptCurrentCell.Y, !MultiSelect, false, false);
                                    Debug.Assert(success);
                                }
                            }
                            else
                            {
                                if (ptCurrentCell.X == -1)
                                {
                                    ClearSelection();
                                    SetSelectedCellCore(lastVisibleColumnIndex, firstVisibleRowIndex, true);
                                    success = ScrollIntoView(lastVisibleColumnIndex, firstVisibleRowIndex, false);
                                    Debug.Assert(success);
                                    if (IsInnerCellOutOfBounds(lastVisibleColumnIndex, firstVisibleRowIndex))
                                    {
                                        return true;
                                    }
                                    success = SetCurrentCellAddressCore(lastVisibleColumnIndex, firstVisibleRowIndex, true, false, false);
                                    Debug.Assert(success);
                                }
                                else
                                {
                                    if (nextVisibleColumnIndex == -1)
                                    {
                                        return true;
                                    }
                                    if (!ScrollIntoView(nextVisibleColumnIndex, ptCurrentCell.Y, true))
                                    {
                                        return true;
                                    }
                                    if (ptCurrentCell.X == -1 || IsColumnOutOfBounds(nextVisibleColumnIndex))
                                    {
                                        return true;
                                    }
                                    if (Columns[ptCurrentCell.X].Selected)
                                    {
                                        ClearSelection();
                                        SetSelectedColumnCore(nextVisibleColumnIndex, true);
                                    }
                                    else
                                    {
                                        ClearSelection();
                                        SetSelectedCellCore(nextVisibleColumnIndex, ptCurrentCell.Y, true);
                                    }
                                    success = SetCurrentCellAddressCore(nextVisibleColumnIndex, ptCurrentCell.Y, true, false, false);
                                    Debug.Assert(success);
                                }
                            }
                        }
                        return true;

                    case DataGridViewSelectionMode.FullRowSelect:
                        if ((keyData & Keys.Control) == Keys.Control)
                        {
                            return ProcessRightMost((keyData & Keys.Shift) == Keys.Shift, lastVisibleColumnIndex, firstVisibleRowIndex);
                        }
                        else
                        {
                            if (ptCurrentCell.X == -1)
                            {
                                ClearSelection();
                                SetSelectedRowCore(firstVisibleRowIndex, true);
                                success = ScrollIntoView(lastVisibleColumnIndex, firstVisibleRowIndex, false);
                                Debug.Assert(success);
                                if (IsInnerCellOutOfBounds(lastVisibleColumnIndex, firstVisibleRowIndex))
                                {
                                    return true;
                                }
                                success = SetCurrentCellAddressCore(lastVisibleColumnIndex, firstVisibleRowIndex, true, false, false);
                                Debug.Assert(success);
                            }
                            else
                            {
                                if (nextVisibleColumnIndex == -1)
                                {
                                    return true;
                                }
                                if (!ScrollIntoView(nextVisibleColumnIndex, ptCurrentCell.Y, true))
                                {
                                    return true;
                                }
                                if (ptCurrentCell.Y == -1 || IsColumnOutOfBounds(nextVisibleColumnIndex))
                                {
                                    return true;
                                }
                                success = SetCurrentCellAddressCore(nextVisibleColumnIndex, ptCurrentCell.Y, true, false, false);
                                Debug.Assert(success);
                            }
                        }
                        return true;
                }
            }
            finally
            {
                NoSelectionChangeCount--;
            }
            return true;
        }

        // Ctrl Right <==> End
        // Shift Ctrl Right <==> Shift End
        private bool ProcessRightMost(bool shift, int lastVisibleColumnIndex, int firstVisibleRowIndex)
        {
            bool success;

            noSelectionChangeCount++;
            try
            {
                switch (SelectionMode)
                {
                    case DataGridViewSelectionMode.CellSelect:
                    case DataGridViewSelectionMode.RowHeaderSelect:
                        if (shift)
                        {
                            if (ptCurrentCell.X == -1)
                            {
                                ClearSelection();
                                SetSelectedCellCore(lastVisibleColumnIndex, firstVisibleRowIndex, true);
                                success = ScrollIntoView(lastVisibleColumnIndex, firstVisibleRowIndex, false);
                                Debug.Assert(success);
                                if (IsInnerCellOutOfBounds(lastVisibleColumnIndex, firstVisibleRowIndex))
                                {
                                    return true;
                                }
                                success = SetCurrentCellAddressCore(lastVisibleColumnIndex, firstVisibleRowIndex, true, false, false);
                                Debug.Assert(success);
                            }
                            else
                            {
                                if (!ScrollIntoView(lastVisibleColumnIndex, ptCurrentCell.Y, true))
                                {
                                    return true;
                                }
                                if (ptCurrentCell.Y == -1 || IsColumnOutOfBounds(lastVisibleColumnIndex))
                                {
                                    return true;
                                }
                                if (MultiSelect)
                                {
                                    Debug.Assert(ptAnchorCell.X >= 0);
                                    if (ptAnchorCell.X == -1)
                                    {
                                        return true;
                                    }
                                    int oldEdgeColumnIndex = ptCurrentCell.X;
                                    int oldEdgeRowIndex = ptCurrentCell.Y;
                                    UpdateSelectedCellsBlock(ptAnchorCell.X, ref oldEdgeColumnIndex, lastVisibleColumnIndex,
                                        ptAnchorCell.Y, ref oldEdgeRowIndex, oldEdgeRowIndex);
                                    success = SetCurrentCellAddressCore(lastVisibleColumnIndex, ptCurrentCell.Y, false, false, false);
                                    Debug.Assert(success);
                                }
                                else
                                {
                                    ClearSelection();
                                    SetSelectedCellCore(lastVisibleColumnIndex, ptCurrentCell.Y, true);
                                    success = SetCurrentCellAddressCore(lastVisibleColumnIndex, ptCurrentCell.Y, true, false, false);
                                    Debug.Assert(success);
                                }
                            }
                        }
                        else
                        {
                            if (ptCurrentCell.X == -1)
                            {
                                ClearSelection();
                                SetSelectedCellCore(lastVisibleColumnIndex, firstVisibleRowIndex, true);
                                success = ScrollIntoView(lastVisibleColumnIndex, firstVisibleRowIndex, false);
                                Debug.Assert(success);
                                if (IsInnerCellOutOfBounds(lastVisibleColumnIndex, firstVisibleRowIndex))
                                {
                                    return true;
                                }
                                success = SetCurrentCellAddressCore(lastVisibleColumnIndex, firstVisibleRowIndex, true, false, false);
                                Debug.Assert(success);
                            }
                            else
                            {
                                if (!ScrollIntoView(lastVisibleColumnIndex, ptCurrentCell.Y, true))
                                {
                                    return true;
                                }
                                if (ptCurrentCell.Y == -1 || IsColumnOutOfBounds(lastVisibleColumnIndex))
                                {
                                    return true;
                                }
                                ClearSelection();
                                SetSelectedCellCore(lastVisibleColumnIndex, ptCurrentCell.Y, true);
                                success = SetCurrentCellAddressCore(lastVisibleColumnIndex, ptCurrentCell.Y, true, false, false);
                                Debug.Assert(success);
                            }
                        }
                        return true;

                    case DataGridViewSelectionMode.FullColumnSelect:
                        if (shift)
                        {
                            if (ptCurrentCell.X == -1)
                            {
                                ClearSelection();
                                SetSelectedColumnCore(lastVisibleColumnIndex, true);
                                success = ScrollIntoView(lastVisibleColumnIndex, firstVisibleRowIndex, false);
                                Debug.Assert(success);
                                if (IsInnerCellOutOfBounds(lastVisibleColumnIndex, firstVisibleRowIndex))
                                {
                                    return true;
                                }
                                success = SetCurrentCellAddressCore(lastVisibleColumnIndex, firstVisibleRowIndex, true, false, false);
                                Debug.Assert(success);
                            }
                            else
                            {
                                if (!ScrollIntoView(lastVisibleColumnIndex, ptCurrentCell.Y, true))
                                {
                                    return true;
                                }
                                if (ptCurrentCell.Y == -1 || IsColumnOutOfBounds(lastVisibleColumnIndex))
                                {
                                    return true;
                                }
                                if (MultiSelect)
                                {
                                    if (ptAnchorCell.X == -1)
                                    {
                                        return true;
                                    }
                                    ClearSelection();
                                    Debug.Assert(ptAnchorCell.X >= 0);
                                    SelectColumnRange(ptAnchorCell.X, lastVisibleColumnIndex, true);
                                    success = SetCurrentCellAddressCore(lastVisibleColumnIndex, ptCurrentCell.Y, false, false, false);
                                    Debug.Assert(success);
                                }
                                else
                                {
                                    SetSelectedColumnCore(ptCurrentCell.X, false);
                                    SetSelectedColumnCore(lastVisibleColumnIndex, true);
                                    success = SetCurrentCellAddressCore(lastVisibleColumnIndex, ptCurrentCell.Y, true, false, false);
                                    Debug.Assert(success);
                                }
                            }
                        }
                        else
                        {
                            if (ptCurrentCell.X == -1)
                            {
                                ClearSelection();
                                SetSelectedColumnCore(lastVisibleColumnIndex, true);
                                success = ScrollIntoView(lastVisibleColumnIndex, firstVisibleRowIndex, false);
                                Debug.Assert(success);
                                if (IsInnerCellOutOfBounds(lastVisibleColumnIndex, firstVisibleRowIndex))
                                {
                                    return true;
                                }
                                success = SetCurrentCellAddressCore(lastVisibleColumnIndex, firstVisibleRowIndex, true, false, false);
                                Debug.Assert(success);
                            }
                            else
                            {
                                if (!ScrollIntoView(lastVisibleColumnIndex, ptCurrentCell.Y, true))
                                {
                                    return true;
                                }
                                if (ptCurrentCell.Y == -1 || IsColumnOutOfBounds(lastVisibleColumnIndex))
                                {
                                    return true;
                                }
                                ClearSelection();
                                SetSelectedColumnCore(lastVisibleColumnIndex, true);
                                success = SetCurrentCellAddressCore(lastVisibleColumnIndex, ptCurrentCell.Y, true, false, false);
                                Debug.Assert(success);
                            }
                        }
                        return true;

                    case DataGridViewSelectionMode.ColumnHeaderSelect:
                        if (shift)
                        {
                            if (ptCurrentCell.X == -1)
                            {
                                ClearSelection();
                                SetSelectedCellCore(lastVisibleColumnIndex, firstVisibleRowIndex, true);
                                success = ScrollIntoView(lastVisibleColumnIndex, firstVisibleRowIndex, false);
                                Debug.Assert(success);
                                if (IsInnerCellOutOfBounds(lastVisibleColumnIndex, firstVisibleRowIndex))
                                {
                                    return true;
                                }
                                success = SetCurrentCellAddressCore(lastVisibleColumnIndex, firstVisibleRowIndex, true, false, false);
                                Debug.Assert(success);
                            }
                            else
                            {
                                if (!ScrollIntoView(lastVisibleColumnIndex, ptCurrentCell.Y, true))
                                {
                                    return true;
                                }
                                if (ptCurrentCell.Y == -1 || IsColumnOutOfBounds(lastVisibleColumnIndex))
                                {
                                    return true;
                                }
                                if (MultiSelect)
                                {
                                    Debug.Assert(ptAnchorCell.X >= 0);
                                    if (ptAnchorCell.X == -1)
                                    {
                                        return true;
                                    }
                                    if (Columns[ptCurrentCell.X].Selected)
                                    {
                                        ClearSelection();
                                        SelectColumnRange(ptAnchorCell.X, lastVisibleColumnIndex, true);
                                    }
                                    else
                                    {
                                        int oldEdgeColumnIndex = ptCurrentCell.X;
                                        int oldEdgeRowIndex = ptCurrentCell.Y;
                                        UpdateSelectedCellsBlock(ptAnchorCell.X, ref oldEdgeColumnIndex, lastVisibleColumnIndex,
                                            ptAnchorCell.Y, ref oldEdgeRowIndex, oldEdgeRowIndex);
                                    }
                                    success = SetCurrentCellAddressCore(lastVisibleColumnIndex, ptCurrentCell.Y, false, false, false);
                                    Debug.Assert(success);
                                }
                                else
                                {
                                    if (Columns[ptCurrentCell.X].Selected)
                                    {
                                        SetSelectedColumnCore(ptCurrentCell.X, false);
                                        SetSelectedColumnCore(lastVisibleColumnIndex, true);
                                    }
                                    else
                                    {
                                        SetSelectedCellCore(ptCurrentCell.X, ptCurrentCell.Y, false);
                                        SetSelectedCellCore(lastVisibleColumnIndex, ptCurrentCell.Y, true);
                                    }
                                    success = SetCurrentCellAddressCore(lastVisibleColumnIndex, ptCurrentCell.Y, true, false, false);
                                    Debug.Assert(success);
                                }
                            }
                        }
                        else
                        {
                            if (ptCurrentCell.X == -1)
                            {
                                ClearSelection();
                                SetSelectedCellCore(lastVisibleColumnIndex, firstVisibleRowIndex, true);
                                success = ScrollIntoView(lastVisibleColumnIndex, firstVisibleRowIndex, false);
                                Debug.Assert(success);
                                if (IsInnerCellOutOfBounds(lastVisibleColumnIndex, firstVisibleRowIndex))
                                {
                                    return true;
                                }
                                success = SetCurrentCellAddressCore(lastVisibleColumnIndex, firstVisibleRowIndex, true, false, false);
                                Debug.Assert(success);
                            }
                            else
                            {
                                if (!ScrollIntoView(lastVisibleColumnIndex, ptCurrentCell.Y, true))
                                {
                                    return true;
                                }
                                if (ptCurrentCell.Y == -1 || IsColumnOutOfBounds(lastVisibleColumnIndex))
                                {
                                    return true;
                                }
                                if (Columns[ptCurrentCell.X].Selected)
                                {
                                    ClearSelection();
                                    SetSelectedColumnCore(lastVisibleColumnIndex, true);
                                }
                                else
                                {
                                    ClearSelection();
                                    SetSelectedCellCore(lastVisibleColumnIndex, ptCurrentCell.Y, true);
                                }
                                success = SetCurrentCellAddressCore(lastVisibleColumnIndex, ptCurrentCell.Y, true, false, false);
                                Debug.Assert(success);
                            }
                        }
                        return true;

                    case DataGridViewSelectionMode.FullRowSelect:
                        if (ptCurrentCell.X == -1)
                        {
                            ClearSelection();
                            SetSelectedRowCore(firstVisibleRowIndex, true);
                            success = ScrollIntoView(lastVisibleColumnIndex, firstVisibleRowIndex, false);
                            Debug.Assert(success);
                            if (IsInnerCellOutOfBounds(lastVisibleColumnIndex, firstVisibleRowIndex))
                            {
                                return true;
                            }
                            success = SetCurrentCellAddressCore(lastVisibleColumnIndex, firstVisibleRowIndex, true, false, false);
                            Debug.Assert(success);
                        }
                        else
                        {
                            if (!ScrollIntoView(lastVisibleColumnIndex, ptCurrentCell.Y, true))
                            {
                                return true;
                            }
                            if (ptCurrentCell.Y == -1 || IsColumnOutOfBounds(lastVisibleColumnIndex))
                            {
                                return true;
                            }
                            success = SetCurrentCellAddressCore(lastVisibleColumnIndex, ptCurrentCell.Y, true, false, false);
                            Debug.Assert(success);
                        }
                        return true;
                }
            }
            finally
            {
                NoSelectionChangeCount--;
            }
            return true;
        }

        protected bool ProcessSpaceKey(Keys keyData)
        {
            if ((keyData & (Keys.Control | Keys.Shift | Keys.Alt)) == Keys.Shift &&
                ptCurrentCell.X != -1)
            {
                noSelectionChangeCount++;
                bool switchedToBulkPaint = false;
                if (selectedBandIndexes.Count > DATAGRIDVIEW_bulkPaintThreshold)
                {
                    inBulkPaintCount++;
                    switchedToBulkPaint = true;
                }
                try
                {
                    if (SelectionMode == DataGridViewSelectionMode.ColumnHeaderSelect)
                    {
                        // Same as clicking the column header cell
                        Debug.Assert(MultiSelect || selectedBandIndexes.Count <= 1);
                        int bandIndex = 0;
                        while (bandIndex < selectedBandIndexes.Count)
                        {
                            if (selectedBandIndexes[bandIndex] != ptCurrentCell.X)
                            {
                                // deselect currently selected column
                                SetSelectedColumnCore(selectedBandIndexes[bandIndex], false);
                            }
                            else
                            {
                                bandIndex++;
                            }
                        }
                        RemoveIndividuallySelectedCells();
                        if (!Columns[ptCurrentCell.X].Selected)
                        {
                            Debug.Assert(!selectedBandIndexes.Contains(ptCurrentCell.X));
                            SetSelectedColumnCore(ptCurrentCell.X, true);
                        }
                        return true;
                    }
                    else if (SelectionMode == DataGridViewSelectionMode.RowHeaderSelect)
                    {
                        // Same as clicking the row header cell
                        Debug.Assert(MultiSelect || selectedBandIndexes.Count <= 1);
                        int bandIndex = 0;
                        while (bandIndex < selectedBandIndexes.Count)
                        {
                            if (selectedBandIndexes[bandIndex] != ptCurrentCell.Y)
                            {
                                // deselect currently selected row
                                SetSelectedRowCore(selectedBandIndexes[bandIndex], false);
                            }
                            else
                            {
                                bandIndex++;
                            }
                        }
                        RemoveIndividuallySelectedCells();
                        if ((Rows.GetRowState(ptCurrentCell.Y) & DataGridViewElementStates.Selected) == 0)
                        {
                            Debug.Assert(!selectedBandIndexes.Contains(ptCurrentCell.Y));
                            SetSelectedRowCore(ptCurrentCell.Y, true);
                        }
                        return true;
                    }
                }
                finally
                {
                    NoSelectionChangeCount--;
                    if (switchedToBulkPaint)
                    {
                        ExitBulkPaint(-1, -1);
                    }
                }
            }
            return false;
        }

        /// <summary>
        ///  Gets a value indicating whether the Tab key should be processed.
        /// </summary>
        protected bool ProcessTabKey(Keys keyData)
        {
            if (StandardTab)
            {
                if ((keyData & Keys.Control) == Keys.Control)
                {
                    if ((keyData & Keys.Shift) == Keys.Shift)
                    {
                        if (!VisibleCellExists || CurrentCellIsFirstVisibleCell)
                        {
                            // Goto previous control
                            return false;
                        }
                        else
                        {
                            // Goto previous cell
                            return TabToPreviousCell();
                        }
                    }
                    else
                    {
                        if (!VisibleCellExists || CurrentCellIsLastVisibleCell)
                        {
                            // Goto next control
                            return false;
                        }
                        else
                        {
                            // Goto next cell
                            return TabToNextCell();
                        }
                    }
                }
                else
                {
                    return false;
                }
            }
            else
            {
                if ((keyData & Keys.Control) == Keys.Control)
                {
                    return false;
                }
                else
                {
                    if ((keyData & Keys.Shift) == Keys.Shift)
                    {
                        if (!VisibleCellExists || CurrentCellIsFirstVisibleCell)
                        {
                            // Goto previous control
                            return false;
                        }
                        else
                        {
                            // Goto previous cell
                            return TabToPreviousCell();
                        }
                    }
                    else
                    {
                        if (!VisibleCellExists || CurrentCellIsLastVisibleCell)
                        {
                            // Goto next control
                            return false;
                        }
                        else
                        {
                            // Goto next cell
                            return TabToNextCell();
                        }
                    }
                }
            }
        }

        /// <summary>
        ///  Processes keys for dataGridView navigation.
        /// </summary>
        protected virtual bool ProcessDataGridViewKey(KeyEventArgs e)
        {
            switch (e.KeyCode)
            {
                case Keys.Tab:
                    {
                        return ProcessTabKey(e.KeyData);
                    }
                case Keys.Up:
                    {
                        return ProcessUpKey(e.KeyData);
                    }
                case Keys.Down:
                    {
                        return ProcessDownKey(e.KeyData);
                    }
                case Keys.Next:
                    {
                        return ProcessNextKey(e.KeyData);
                    }
                case Keys.Prior:
                    {
                        return ProcessPriorKey(e.KeyData);
                    }
                case Keys.Left:
                    {
                        return ProcessLeftKey(e.KeyData);
                    }
                case Keys.Right:
                    {
                        return ProcessRightKey(e.KeyData);
                    }
                case Keys.F2:
                    {
                        return ProcessF2Key(e.KeyData);
                    }
                case Keys.F3:
                    {
                        return ProcessF3Key(e.KeyData);
                    }
                case Keys.F10:
                    {
                        if (e.Control && e.Shift)
                        {
                            return ProcessControlShiftF10Keys(e.KeyData);
                        }
                        break;
                    }
                case Keys.Home:
                    {
                        return ProcessHomeKey(e.KeyData);
                    }
                case Keys.D0:
                case Keys.NumPad0:
                    {
                        return ProcessZeroKey(e.KeyData);
                    }
                case Keys.Delete:
                    {
                        return ProcessDeleteKey(e.KeyData);
                    }
                case Keys.End:
                    {
                        return ProcessEndKey(e.KeyData);
                    }
                case Keys.Enter:
                    {
                        return ProcessEnterKey(e.KeyData);
                    }
                case Keys.Escape:
                    {
                        return ProcessEscapeKey(e.KeyData);
                    }
                case Keys.A:
                    {
                        return ProcessAKey(e.KeyData);
                    }
                case Keys.C:
                case Keys.Insert:
                    {
                        return ProcessInsertKey(e.KeyData);
                    }
                case Keys.Space:
                    {
                        return ProcessSpaceKey(e.KeyData);
                    }
            }
            return false;
        }

        protected bool ProcessUpKey(Keys keyData)
        {
            DataGridViewColumn dataGridViewColumn = Columns.GetFirstColumn(DataGridViewElementStates.Visible);
            int firstVisibleColumnIndex = (dataGridViewColumn == null) ? -1 : dataGridViewColumn.Index;
            int firstVisibleRowIndex = Rows.GetFirstRow(DataGridViewElementStates.Visible);
            if (firstVisibleColumnIndex == -1 || firstVisibleRowIndex == -1)
            {
                return false;
            }
            int previousVisibleRowIndex = -1;
            if (ptCurrentCell.Y != -1)
            {
                previousVisibleRowIndex = Rows.GetPreviousRow(ptCurrentCell.Y, DataGridViewElementStates.Visible);
            }

            noSelectionChangeCount++;
            try
            {
                switch (SelectionMode)
                {
                    case DataGridViewSelectionMode.CellSelect:
                    case DataGridViewSelectionMode.ColumnHeaderSelect:
                        if ((keyData & Keys.Control) == Keys.Control)
                        {
                            if ((keyData & Keys.Shift) == Keys.Shift)
                            {
                                if (ptCurrentCell.X == -1)
                                {
                                    ClearSelection();
                                    SetSelectedCellCore(firstVisibleColumnIndex, firstVisibleRowIndex, true);
                                    ScrollIntoView(firstVisibleColumnIndex, firstVisibleRowIndex, false);
                                    if (IsInnerCellOutOfBounds(firstVisibleColumnIndex, firstVisibleRowIndex))
                                    {
                                        return true;
                                    }

                                    SetCurrentCellAddressCore(firstVisibleColumnIndex, firstVisibleRowIndex, true, false, false);
                                }
                                else
                                {
                                    if (MultiSelect)
                                    {
                                        if (!ScrollIntoView(ptCurrentCell.X, firstVisibleRowIndex, true))
                                        {
                                            return true;
                                        }
                                        if (ptAnchorCell.X == -1 || ptCurrentCell.X == -1 ||
                                            IsRowOutOfBounds(firstVisibleRowIndex))
                                        {
                                            return true;
                                        }
                                        Debug.Assert(ptAnchorCell.Y >= 0);
                                        int oldEdgeColumnIndex = ptCurrentCell.X;
                                        int oldEdgeRowIndex = ptCurrentCell.Y;
                                        UpdateSelectedCellsBlock(ptAnchorCell.X, ref oldEdgeColumnIndex, oldEdgeColumnIndex,
                                            ptAnchorCell.Y, ref oldEdgeRowIndex, firstVisibleRowIndex);
                                        SetCurrentCellAddressCore(ptCurrentCell.X, firstVisibleRowIndex, false, false, false);
                                    }
                                    else
                                    {
                                        if (!ScrollIntoView(ptCurrentCell.X, firstVisibleRowIndex, true))
                                        {
                                            return true;
                                        }
                                        if (ptCurrentCell.X == -1 || IsRowOutOfBounds(firstVisibleRowIndex))
                                        {
                                            return true;
                                        }
                                        ClearSelection();
                                        SetSelectedCellCore(ptCurrentCell.X, firstVisibleRowIndex, true);
                                        SetCurrentCellAddressCore(ptCurrentCell.X, firstVisibleRowIndex, true, false, false);
                                    }
                                }
                            }
                            else
                            {
                                if (ptCurrentCell.X == -1)
                                {
                                    ClearSelection();
                                    SetSelectedCellCore(firstVisibleColumnIndex, firstVisibleRowIndex, true);
                                    ScrollIntoView(firstVisibleColumnIndex, firstVisibleRowIndex, false);
                                    if (IsInnerCellOutOfBounds(firstVisibleColumnIndex, firstVisibleRowIndex))
                                    {
                                        return true;
                                    }

                                    SetCurrentCellAddressCore(firstVisibleColumnIndex, firstVisibleRowIndex, true, false, false);
                                }
                                else
                                {
                                    if (!ScrollIntoView(ptCurrentCell.X, firstVisibleRowIndex, true))
                                    {
                                        return true;
                                    }
                                    if (ptCurrentCell.X == -1 || IsRowOutOfBounds(firstVisibleRowIndex))
                                    {
                                        return true;
                                    }
                                    ClearSelection();
                                    SetSelectedCellCore(ptCurrentCell.X, firstVisibleRowIndex, true);
                                    SetCurrentCellAddressCore(ptCurrentCell.X, firstVisibleRowIndex, true, false, false);
                                }
                            }
                        }
                        else
                        {
                            if ((keyData & Keys.Shift) == Keys.Shift)
                            {
                                if (ptCurrentCell.X == -1)
                                {
                                    ClearSelection();
                                    SetSelectedCellCore(firstVisibleColumnIndex, firstVisibleRowIndex, true);
                                    ScrollIntoView(firstVisibleColumnIndex, firstVisibleRowIndex, false);
                                    if (IsInnerCellOutOfBounds(firstVisibleColumnIndex, firstVisibleRowIndex))
                                    {
                                        return true;
                                    }

                                    SetCurrentCellAddressCore(firstVisibleColumnIndex, firstVisibleRowIndex, true, false, false);
                                }
                                else
                                {
                                    if (previousVisibleRowIndex == -1)
                                    {
                                        return true;
                                    }
                                    if (!ScrollIntoView(ptCurrentCell.X, previousVisibleRowIndex, true))
                                    {
                                        return true;
                                    }
                                    if (ptCurrentCell.X == -1 || IsRowOutOfBounds(previousVisibleRowIndex))
                                    {
                                        return true;
                                    }
                                    if (MultiSelect)
                                    {
                                        Debug.Assert(ptAnchorCell.Y >= 0);
                                        if (ptAnchorCell.Y == -1)
                                        {
                                            return true;
                                        }
                                        int oldEdgeColumnIndex = ptCurrentCell.X;
                                        int oldEdgeRowIndex = ptCurrentCell.Y;
                                        UpdateSelectedCellsBlock(ptAnchorCell.X, ref oldEdgeColumnIndex, oldEdgeColumnIndex,
                                            ptAnchorCell.Y, ref oldEdgeRowIndex, previousVisibleRowIndex);
                                    }
                                    else
                                    {
                                        ClearSelection();
                                        SetSelectedCellCore(ptCurrentCell.X, previousVisibleRowIndex, true);
                                    }

                                    SetCurrentCellAddressCore(ptCurrentCell.X, previousVisibleRowIndex, !MultiSelect, false, false);
                                }
                            }
                            else
                            {
                                if (ptCurrentCell.X == -1)
                                {
                                    ClearSelection();
                                    SetSelectedCellCore(firstVisibleColumnIndex, firstVisibleRowIndex, true);
                                    ScrollIntoView(firstVisibleColumnIndex, firstVisibleRowIndex, false);
                                    if (IsInnerCellOutOfBounds(firstVisibleColumnIndex, firstVisibleRowIndex))
                                    {
                                        return true;
                                    }

                                    SetCurrentCellAddressCore(firstVisibleColumnIndex, firstVisibleRowIndex, true, false, false);
                                }
                                else
                                {
                                    if (previousVisibleRowIndex == -1)
                                    {
                                        return true;
                                    }
                                    if (!ScrollIntoView(ptCurrentCell.X, previousVisibleRowIndex, true))
                                    {
                                        return true;
                                    }
                                    if (ptCurrentCell.X == -1 || IsRowOutOfBounds(previousVisibleRowIndex))
                                    {
                                        return true;
                                    }
                                    ClearSelection();
                                    SetSelectedCellCore(ptCurrentCell.X, previousVisibleRowIndex, true);
                                    SetCurrentCellAddressCore(ptCurrentCell.X, previousVisibleRowIndex, true, false, false);
                                }
                            }
                        }
                        return true;

                    case DataGridViewSelectionMode.FullRowSelect:
                        if ((keyData & Keys.Control) == Keys.Control)
                        {
                            if ((keyData & Keys.Shift) == Keys.Shift)
                            {
                                if (ptCurrentCell.X == -1)
                                {
                                    ClearSelection();
                                    SetSelectedRowCore(firstVisibleRowIndex, true);
                                    ScrollIntoView(firstVisibleColumnIndex, firstVisibleRowIndex, false);
                                    if (IsInnerCellOutOfBounds(firstVisibleColumnIndex, firstVisibleRowIndex))
                                    {
                                        return true;
                                    }

                                    SetCurrentCellAddressCore(firstVisibleColumnIndex, firstVisibleRowIndex, true, false, false);
                                }
                                else
                                {
                                    if (MultiSelect)
                                    {
                                        if (!ScrollIntoView(ptCurrentCell.X, firstVisibleRowIndex, true))
                                        {
                                            return true;
                                        }
                                        if (ptAnchorCell.X == -1 || ptCurrentCell.X == -1 ||
                                            IsRowOutOfBounds(firstVisibleRowIndex))
                                        {
                                            return true;
                                        }
                                        ClearSelection();
                                        Debug.Assert(ptAnchorCell.Y >= 0);
                                        SelectRowRange(firstVisibleRowIndex, ptAnchorCell.Y, true);
                                        SetCurrentCellAddressCore(ptCurrentCell.X, firstVisibleRowIndex, false, false, false);
                                    }
                                    else
                                    {
                                        if (!ScrollIntoView(ptCurrentCell.X, firstVisibleRowIndex, true))
                                        {
                                            return true;
                                        }
                                        if (ptCurrentCell.X == -1 || IsRowOutOfBounds(firstVisibleRowIndex))
                                        {
                                            return true;
                                        }
                                        SetSelectedRowCore(ptCurrentCell.Y, false);
                                        SetSelectedRowCore(firstVisibleRowIndex, true);
                                        SetCurrentCellAddressCore(ptCurrentCell.X, firstVisibleRowIndex, true, false, false);
                                    }
                                }
                            }
                            else
                            {
                                if (ptCurrentCell.X == -1)
                                {
                                    ClearSelection();
                                    SetSelectedRowCore(firstVisibleRowIndex, true);
                                    ScrollIntoView(firstVisibleColumnIndex, firstVisibleRowIndex, false);
                                    if (IsInnerCellOutOfBounds(firstVisibleColumnIndex, firstVisibleRowIndex))
                                    {
                                        return true;
                                    }

                                    SetCurrentCellAddressCore(firstVisibleColumnIndex, firstVisibleRowIndex, true, false, false);
                                }
                                else
                                {
                                    if (!ScrollIntoView(ptCurrentCell.X, firstVisibleRowIndex, true))
                                    {
                                        return true;
                                    }
                                    if (ptCurrentCell.X == -1 || IsRowOutOfBounds(firstVisibleRowIndex))
                                    {
                                        return true;
                                    }
