            if (rowIndex < 0 || rowIndex >= Rows.Count)
            {
                throw new ArgumentOutOfRangeException(nameof(rowIndex));
            }
            if (IsHandleCreated)
            {
                OnCellCommonChange(columnIndex, rowIndex);
            }
