                    }
                    else
                    {
                        Debug.Assert(rowIndex < this.Rows.Count && columnIndex < this.Columns.Count);
                        dataGridViewCell = this.Rows.SharedRow(rowIndex).Cells[columnIndex];
                    }
                }
            }
        }

