                BuildInheritedColumnHeaderCellStyle(inheritedCellStyle, cell);
                Rectangle cellBounds = this.layout.TopLeftHeader;
                cellBounds.Width = this.rowHeadersWidth;
                cellBounds.Height = this.columnHeadersHeight;
