            SuppressMessage("Microsoft.Performance", "CA1801:AvoidUnusedParameters") // Derived controls may need keyData.
        ]
        protected bool ProcessF2Key(Keys keyData)
        {
            if (this.ptCurrentCell.X != -1 && 
